/*
 * Objects
 */
.o-layout-h {
  display: flex;
  flex-direction: row;
  align-items: center;
}
.o-container {
  position: relative;
  width: 940px;
  margin: 0 auto;
}
/*
 * Elements
 */
html {
  font-size: 11px;
  height: 100%;
}
body {
  min-height: 100%;
  background: url("/public/colored_body_top2.png") center top no-repeat #1b2838;
}
a {
  text-decoration: none;
  cursor: pointer;
}
li {
  list-style: none;
}
/*
 * Components
 */
.c-btn {
  cursor: pointer;
  display: inline-block;
}
.c-btn--grey {
  color: #b8b6b4;
}
.c-btn--grey:hover {
  color: #fff;
}
.c-btn--green {
  color: #e5e4dc;
  background: #5c7e10;
}
.c-btn--green:hover {
  color: #fff;
}
.c-btn--narrow {
  padding: 7px;
}
.c-btn--wide {
  padding: 9px;
}
.c-btn--inverse {
  padding: 1px 16px;
  font-size: 12px;
  line-height: 20px;
  border: 1px solid rgba(255,255,255,0.4);
  border-radius: 2px;
  color: #fff;
}
.c-btn--inverse:hover {
  border: 1px solid #fff;
}
.c-nav {
  display: flex;
  flex-direction: row;
  align-items: center;
}
.c-nav--dark {
  box-shadow: 0 0 3px rgba(0,0,0,0.4);
  background: rgba(48,95,128,0.9);
}
.c-nav__item {
  position: relative;
}
.c-nav__item__btn {
  display: inline-block;
  cursor: pointer;
  vertical-align: middle;
}
.c-nav__item__btn--grey {
  color: #b8b6b4;
  padding: 10px;
}
.c-nav__item__btn--grey:hover {
  color: #fff;
}
.c-nav__item__btn--dark {
  padding: 0 10px;
  color: #d9dadd;
  border-right: 1px solid rgba(16,21,25,0.3);
  background: rgba(255,255,255,0);
  font-size: 13px;
  line-height: 35px;
}
.c-nav__item__btn--dark:hover {
  color: #000;
  background: rgba(255,255,255,0.8);
}
.c-nav__item__btn--big {
  font-size: 14px;
}
.c-dropdown {
  position: absolute;
  display: flex;
  flex-direction: column;
  z-index: 1500;
  transition: all 0.5s;
}
.c-dropdown--active {
  opacity: 1;
  visibility: visible;
}
.c-dropdown--deactive {
  opacity: 0;
  visibility: hidden;
}
.c-dropdown--dark {
  background: #171a21;
  padding: 5px 15px 10px 15px;
  box-shadow: 3px 3px 5px -3px #000;
}
.c-dropdown--light {
  background: linear-gradient(to bottom, #e3eaef 5%, #c7d5e0 95%);
  padding: 8px 5px;
  box-shadow: 0 0 12px #000;
}
.c-dropdown__item {
  cursor: pointer;
  white-space: nowrap;
}
.c-dropdown__item--small {
  font-size: 11px;
}
.c-dropdown__item--big {
  font-size: 13px;
  padding: 5px 12px;
}
.c-dropdown__item--dark {
  color: #000;
}
.c-dropdown__item--dark:hover {
  color: #fff;
  background: #222e3c;
}
.c-dropdown__item--blue {
  color: #4f94bc;
}
.c-dropdown__hr {
  height: 1px;
  margin: 5px 10px;
  background: #fff;
}
.c-slider__container {
  position: relative;
  cursor: pointer;
}
.c-slider__main {
  display: flex;
  transition: all 0.5s;
  position: absolute;
  left: 0;
  top: 0;
}
.c-slider__main-active {
  opacity: 1;
  visibility: visible;
}
.c-slider__main-deactive {
  opacity: 0;
  visibility: hidden;
}
.c-slider__item {
  background: linear-gradient(135deg, #25313f 5%, #36617d 95%);
}
.c-slider__arrow-container {
  position: absolute;
  top: calc(50% - 54px);
  width: 45px;
  height: 108px;
  display: flex;
  justify-content: center;
  align-items: center;
  cursor: pointer;
}
.c-slider__arrow-container--left {
  left: -45px;
  background: linear-gradient(to right, rgba(0,0,0,0.3) 5%, rgba(0,0,0,0) 95%);
}
.c-slider__arrow-container--left:hover {
  background: linear-gradient(to right, rgba(171,218,244,0.3) 5%, rgba(171,218,244,0) 95%);
}
.c-slider__arrow-container--right {
  right: -45px;
  background: linear-gradient(to right, rgba(0,0,0,0) 5%, rgba(0,0,0,0.3) 95%);
}
.c-slider__arrow-container--right:hover {
  background: linear-gradient(to right, rgba(171,218,244,0) 5%, rgba(171,218,244,0.3) 95%);
}
.c-slider__arrow {
  width: 23px;
  height: 36px;
  background-image: url("/public/arrows.png");
}
.c-slider__arrow--left {
  left: 0;
  background-position-x: 23px;
}
.c-slider__arrow--right {
  right: 0;
}
.c-slider__thumbs-container {
  display: flex;
  justify-content: center;
  align-items: center;
  height: 40px;
}
.c-slider__thumb {
  cursor: pointer;
  width: 15px;
  height: 9px;
  background-color: rgba(255,255,255,0.2);
  border-radius: 2px;
  transition: all 0.5s;
  margin: 0 2px;
}
.c-slider__thumb:hover {
  background-color: rgba(255,255,255,0.4);
}
.c-slider__thumb--active {
  background-color: rgba(255,255,255,0.4);
}
